import React from 'react';
import styled from 'styled-components';
import {Container, Button, Row} from 'react-bootstrap';

const FmuiButton = styled(Button)`
    height: auto;
`;

/*const Floor = ({floor, className}) =>
    <FmuiButton className={`${className}`}>{floor}</FmuiButton>
*/
const Box = styled(Button)`${({square}) => `
    display:flex;
    align-items:center;
    justify-content:center;
    ${square ? `    
        position: relative;

        &:before {
            content:"";
            float: left;
            padding-top:100%;
        }
    ` : "" }
`}`

const Content = styled.div`
    font-size:50pt;
`

const Floor = ({floor, square, className}) => <Box square={square} className={`${className}`}>
    <Content>{floor}</Content>
</Box>

const Floors = ({floors}) => <Container className="p-3">{
    floors.map((floor,i,a) => i % 2 === 0 && (
        a[i+1] != undefined ? 
            <Row style={{"justify-content":"space-evenly"}}  className="mb-3 d-flex">
                <Floor className="col-5" floor={floor} square/>
                <Floor className="col-5" floor={a[i+1]} square/>
            </Row> 
            : 
            <Row className="d-flex" >
                <Floor className="col-10" floor={floor}/>
            </Row>
        )
    )
}</Container>

export default Floors;
