import { Button, FormControl, InputGroup } from 'react-bootstrap';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import Coming from './Coming';
import backend from '../../../backend';
import ErrorAlert from '../../utils/components/ErrorAlert';
import { FormattedMessage } from 'react-intl';
import Alert from '../../utils/components/Alert';


const Password = () => {
    const {floor} = useParams();
    const [floors, setFloors] = useState(undefined);
    const [token, setToken] = useState();
    const [pass, setPass] = useState();
    const [coming, setComing] = useState(false);
    const [invalidPass, setInvalidPass] = useState(false);
    
    useEffect(() => {
        backend.getFloors(id,setFloors);
    }, [id]);

    return coming ? <Coming token={token} floors={floors}/> : <form>

        <Alert heading="project.text.password"/>

        <InputGroup>
        <FormattedMessage id="project.placeholder.password">{placeholder =>
                <FormControl
                placeholder={placeholder}
                pattern="\d{4}"
                onChange={e => setPass(e.target.value)}
            />
        }</FormattedMessage>

            <InputGroup.Append>
                <Button variant="outline-primary" onClick={_e => {
                    backend.getToken(id, pass, token => {
                        setToken(token);
                        backend.move(token, floor, r => {
                            console.log(r);
                            setComing(true);
                        }, e => setComing(true)); // TEA-POT
                    }, e => {setInvalidPass(true)})
                }}>submit</Button>
            </InputGroup.Append>
        </InputGroup>

        {invalidPass && <ErrorAlert
            heading="project.alerts.password.incorrect.header" 
            text="project.alerts.password.incorrect.body" 
            onClose={() => setInvalidPass(false)}
            style={{marginTop:"20px"}}/>
        }

    </form>
};

export default Password;
