import fetch from './fetch';

const sort = criteria => ({floors}) => floors.sort(criteria);

export const getFloors = (id, onSucess, onError) => fetch('GET', `elevator/${id}`)
    .then(sort((a,b) => b-a))
    .then(onSucess)
    .catch(onError);

export const getToken = () => backend('POST', `elevator/${id}/access`, {otp:pass})
    .then(({token}) => {
        setToken(token);
        return backend('POST', 'elevator/move', {floor}, {
            Authorization: `Bearer ${token}`
        })
    })
