import { Alert, Button, FormControl, InputGroup } from 'react-bootstrap';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import backend from '../../../backend/fetch';
import Coming from './Coming';
import { getFloors, getToken } from '../../../backend/service';


const Password = () => {
    const {id, floor} = useParams();
    const [floors, setFloors] = useState();
    const [token, setToken] = useState();
    const [pass, setPass] = useState();
    const [coming, setComing] = useState(false);
    
    useEffect(() => {
        getFloors(id, setFloors);
    }, []);

    return coming ? <Coming token={token} floors={floors}/> : <form>

        <Alert variant="primary">Password:</Alert>
        <InputGroup>
            <FormControl
                placeholder="password"
                pattern="\d{4}"
                onChange={e => setPass(e.target.value)}
            />
            <InputGroup.Append>
                <Button variant="outline-primary" onClick={_e => {
                    getToken(id, pass, ({token}) => {
                        setToken(token);
                        return backend('POST', 'elevator/move', {floor}, {
                            Authorization: `Bearer ${token}`
                        })
                    })
                    backend('POST', `elevator/${id}/access`, {otp:pass})
                        .then()
                        .then(r =>{
                            console.log(r);
                            setComing(true)
                        })
                        .catch(console.err);
                }}>submit</Button>
            </InputGroup.Append>
        </InputGroup>

    </form>
};

export default Password;
