import React from 'react';
import { Alert } from 'react-bootstrap';

const myAlert = ({heading, text, onClose}) => {

    return <Alert variant="primary" onClose={onClose} dismissible={onClose ? true : false}>
    {heading && <Alert.Heading>{heading}</Alert.Heading>}
    {text && <p>{text}</p>}
</Alert>}

export default myAlert;
