import React, { useState } from 'react';
import { Alert, Button } from 'react-bootstrap';
import myAlert from '../../utils/components/Alert';
import Floors from './Floors';

const Coming = ({floors}) => {
    const [pressed, setPressed] = useState();

    return pressed ? <div><Floors floors={floors}/></div> 
    : <div>
        <myAlert heading="project.alerts.elevator.coming" />
        <Alert>
            El ascensor está viniendo.
        </Alert>
        <Button onClick={() => {setPressed(true)}}>
            Pulse cuando esté en el ascensor.
        </Button>
    </div>
}
export default Coming;
