import { Alert, Button, FormControl, InputGroup } from 'react-bootstrap';
import React, { useState } from 'react';
import { useParams } from 'react-router';
import Floors from './Floors';
import backend from '../../../backend/fetch';

const Password = () => {
    const {id, floor} = useParams();
    const [pass, setPass] = useState();
    const [floors, setFloors] = useState();
    const [coming, setComing] = useState(false);

    return floors ? <div><Floors floors={floors}/></div> : <form>

        <Alert variant="primary">Password:</Alert>
        <InputGroup>
            <FormControl
                placeholder="password"
                pattern="\d{4}"
                onChange={e => setPass(e.target.value)}
            />
            <InputGroup.Append>
                <Button variant="outline-primary" onClick={_e => {
                    backend('POST', `elevator/${id}/to/${floor}`)
                        .then(r => setComing(true))
                        .catch()
                    backend('GET', `elevator/${id}`, {otp: pass})
                        .then(r => r.json())
                        .then(resp => {
                            setFloors(resp.sort((a,b) => b-a))
                        })
                        .catch(console.err);
                }}>submit</Button>
            </InputGroup.Append>
        </InputGroup>

    </form>
};

export default Password;
