import React, { useState } from 'react';
import { Alert, Button } from 'react-bootstrap';
import Floors from './Floors';
import backend from "../../../backend/fetch";

const Coming = ({token, id}) => {
    const [floors, setFloors] = useState();

    return floors ? <div><Floors token={token} floors={floors}/></div> 
    : <div>
        <Alert>
            El ascensor está viniendo.
        </Alert>
        <Button onClick={() => {
            backend('GET', `elevator/${id}`)
                .then(r => r.json())
                .then(resp => {
                    setFloors(resp.sort((a,b) => b-a))
                })
                .catch(console.err);
        }}>
            Pulse el botón cuando esté en el ascensor.
        </Button>
    </div>
}
export default Coming;
