const ENDPOINT_ACCESS = "http://192.168.0.14:3001";

const toJSON = r => r.json();

const petition = (method='GET', url="", body, header={}) => fetch(`${ENDPOINT_ACCESS}/${url}`, {
    method,
    body: body ? JSON.stringify(body) : null,
    headers:{
        'Content-Type': 'application/json',
        ...header
    }
}).then(toJSON);


export default petition;
