import { appFetch, setReauthenticationCallback, setServiceToken } from './appFetch';
import fetch from './fetch';

const sort = criteria => ({floors}) => floors.sort(criteria);

export const getFloors = (id, onSucess=console.log, onError=console.err) => {
    appFetch(`elevator/${id}`, config('GET'), s => {
        onSucess(s.floors.sort((a,b) => b-a));
    }, onError);
}
export const getToken = (id, otp, onSucess=console.log, onError=console.err) => {
    appFetch(`elevator/${id}/access`, config('POST', {otp}), ({token}) => {
        setServiceToken(token);
        onSucess(token);
    }, onError);
}
export const move = (token, floor, onSucess=console.log, onError=console.err) => {
    appFetch('elevator/move', config('POST', {floor}))
    fetch('POST', 'elevator/move', {floor},
    onSucess,onError);
}
