import { Alert, Button, FormControl, InputGroup } from 'react-bootstrap';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import Coming from './Coming';
import backend from '../../../backend';
import ErrorAlert from './ErrorAlert';


const Password = () => {
    const {id, floor} = useParams();
    const [floors, setFloors] = useState();
    const [notExists, setNotExists] = useState(false);
    const [token, setToken] = useState();
    const [pass, setPass] = useState();
    const [coming, setComing] = useState(false);
    const [invalidPass, setInvalidPass] = useState(false);
    
    useEffect(() => {
        backend.getFloors(id, setFloors, e => {setNotExists(true)});
    }, [id]);

    return notExists ? <h1>El ascensor con id {id} no existe. </h1> : coming ? <Coming token={token} floors={floors}/> : <form>

        <Alert variant="primary">Password:</Alert>
        <InputGroup>
            <FormControl
                placeholder="password"
                pattern="\d{4}"
                onChange={e => setPass(e.target.value)}
            />
            <InputGroup.Append>
                <Button variant="outline-primary" onClick={_e => {
                    backend.getToken(id, pass, (token) => {
                        setToken(token);
                        backend.move(token, floor, r => {
                            console.log(r);
                            setComing(true);
                        }, e => setComing(true)); // TEA-POT
                    }, e => {setInvalidPass(true)})
                }}>submit</Button>
            </InputGroup.Append>
        </InputGroup>

        {invalidPass && <ErrorAlert onClose={() => setInvalidPass(false)} heading={"The code you introduced is incorrect."} text={"Please introduce the right password of the elevator."} />}

    </form>
};

export default Password;
