const ENDPOINT_ACCESS = "http://localhost:3001";

const petition = (method='GET', url="", body) => fetch(`${ENDPOINT_ACCESS}/${url}`, {
    method,
    body: body ? JSON.stringify(body) : null,
    headers:{
        'Content-Type': 'application/json'
    }
});

export default petition;
