import React from 'react';
import styled from 'styled-components';
import {Container, Button, Row} from 'react-bootstrap';

const Box = styled(Button)`${({square}) => `
    display:flex;
    align-items:center;
    justify-content:center;
    ${square ? `    
        position: relative;

        &:before {
            content:"";
            float: left;
            padding-top:100%;
        }
    ` : "" }
`}`

const Content = styled.div`
    font-size:50pt;
`

const Floor = ({floor, square, className}) => <Box square={square} className={`${className}`} onClick={() => {
    backend('POST', `elevator/${id}/to`, )
    .then(r => r.json())
    .then(resp => {
        setFloors(resp.sort((a,b) => b-a))
    })
    .catch(console.err);
    }}>
        <Content>{floor}</Content>
</Box>

const Raul = styled(Row)`
    justify-content:space-evenly;
`
const Floors = ({floors}) => <Container className="p-3">{
    floors.map((floor,i,a) => i % 2 === 0 && (
        a[i+1] !== undefined ? 
            <Raul className="mb-3 d-flex">
                <Floor className="col-5" floor={floor} square/>
                <Floor className="col-5" floor={a[i+1]} square/>
            </Raul> 
            : 
            <Raul className="d-flex" >
                <Floor className="col-10" floor={floor}/>
            </Raul>
        )
    )
}</Container>

export default Floors;
