import { Alert, Button } from 'react-bootstrap';
import React, { useState } from 'react';
import { useParams } from 'react-router';
import Floors from './Floors';

const Password = () => {
    const {id} = useParams();
    const [pass, setPass] = useState();
    const [floors, setFloors] = useState();

    return floors ? <div><Floors floors={floors}/></div> : <form>

        <Alert>password:</Alert>
        <input placeholder="password" onChange={e => setPass(e.target.value)} />
        <hr/>
        <Button onClick={e => {
            e.preventDefault();
            console.log(id, pass, JSON.stringify({otp: pass}));
            fetch(`http://localhost:3001/access/${id}`, {
                method: 'POST',
                body: JSON.stringify({otp: pass}),
                headers:{
                    'Content-Type': 'application/json'
                }
            })
            .then(r => r.json())
            .then(resp => setFloors(resp.sort((a,b) => b-a)))
            .catch(console.err);
        }}>SUBMIT</Button>
    </form>
};

export default Password;
