import React, { useState } from 'react';
import Floors from './Floors';




const App = () => {
    const [id, setId] = useState();
    const [pass, setPass] = useState();
    const [floors, setFloors] = useState();

    return floors ? <div><Floors floors={floors}/></div> : <form>
        <h1>ID ascensor:</h1>
        <input placeholder="ID ascensor" onChange={e => setId(e.target.value)}/>

        <h1>password:</h1>
        <input placeholder="password" onChange={e => setPass(e.target.value)} />
        <hr/>
        <button onClick={e => {
            e.preventDefault();
            console.log(id, pass, JSON.stringify({otp: pass}));
            fetch(`http://localhost:3001/access/${id}`, {
                method: 'POST',
                body: JSON.stringify({otp: pass}),
                headers:{
                    'Content-Type': 'application/json'
                }
            })
            .then(r => r.json())
            .then(resp => setFloors(resp.sort((a,b) => b-a)))
            .catch(console.err);
        }}><h3>SUBMIT</h3></button>
    </form>};

export default App;
