import React from 'react';
import { Alert } from 'react-bootstrap';

const ErrorAlert = ({heading, text, onClose, style}) => <Alert variant="danger" onClose={onClose} dismissible={onClose ? true : false}>
    {heading && <Alert.Heading>{heading}</Alert.Heading>}
    {text && <p>{text}</p>}
</Alert>

export default ErrorAlert;
