import React, { useState } from 'react';
import styled from 'styled-components';
import {Container, Button, Row} from 'react-bootstrap';
import backend from '../../../backend/';

const Box = styled(Button)`${({square}) => `
    display:flex;
    align-items:center;
    justify-content:center;
    ${square ? `    
        position: relative;

        &:before {
            content:"";
            float: left;
            padding-top:100%;
        }
    ` : "" }
`}`

const Content = styled.div`
    font-size:50pt;
`

const Floor = ({floor, square, className, token, onSucess=console.log, clicked=c=>{}}) => <Box square={square} className={`${className}`} onClick={() => {
        backend.move(token, floor, onSucess)
        clicked(true);
    }}>
        <Content>{floor}</Content>
</Box>

const Raul = styled(Row)`
    justify-content:space-evenly;
`
const Floors = ({floors, token}) =>{
    const [clicked, setClicked] = useState(false);
    return <Container className="p-3">{
    floors.map((floor,i,a) => i % 2 === 0 && (
        a[i+1] !== undefined ? 
            <Raul key={i} className="mb-3 d-flex">
                <Floor token={token} className="col-5" floor={floor}  square/>
                <Floor token={token} className="col-5" floor={a[i+1]} square/>
            </Raul> 
            : 
            <Raul key={i} className="d-flex" >
                <Floor token={token} className="col-10" floor={floor}/>
            </Raul>
        )
    )
}</Container>}

export default Floors;
