import { Alert, Button, FormControl, InputGroup } from 'react-bootstrap';
import React, { useState } from 'react';
import { useParams } from 'react-router';
import Floors from './Floors';

const Password = () => {
    const {id} = useParams();
    const [pass, setPass] = useState();
    const [floors, setFloors] = useState();

    return floors ? <div><Floors floors={floors}/></div> : <form>

        <Alert variant="primary">Password:</Alert>
        <InputGroup>
            <FormControl
                placeholder="password"
                title="Please, introduce a valid password."
                pattern=".{8,}"
                onChange={e => setPass(e.target.value)}
            />
            <InputGroup.Append>
                <Button variant="outline-primary" onClick={e => {
                    console.log(id, pass, JSON.stringify({otp: pass}));
                    fetch(`http://localhost:3001/access/${id}`, {
                        method: 'POST',
                        body: JSON.stringify({otp: pass}),
                        headers:{
                            'Content-Type': 'application/json'
                        }
                    })
                    .then(r => r.json())
                    .then(resp => setFloors(resp.sort((a,b) => b-a)))
                    .catch(console.err);
                }}>submit</Button>
            </InputGroup.Append>
        </InputGroup>

    </form>
};

export default Password;
