import fetch from './fetch';

const sort = criteria => ({floors}) => floors.sort(criteria);

export const getFloors = (id, onSucess=console.log, onError=console.err) => fetch('GET', `elevator/${id}`)
    .then(sort((a,b) => b-a))
    .then(onSucess)
    .catch(onError);

export const getToken = (id, otp, onSucess=console.log, onError=console.err) => backend('POST', `elevator/${id}/access`, {otp})
    .then(onSucess)
    .catch(onError);

export const move = (token, floor, onSucess=console.log, onError=console.err) => backend('POST', 'elevator/move', {floor}, {
        Authorization: `Bearer ${token}`
    }).then(onSucess)
    .catch(onError);
