import fetch from './fetch';

const sort = criteria => ({floors}) => floors.sort(criteria);

export const getFloors = (id, onSucess, onError) => fetch('GET', `elevator/${id}`)
    .then(sort((a,b) => b-a))
    .then(onSucess)
    .catch(onError);

export const getToken = (id, otp) => backend('POST', `elevator/${id}/access`, {otp})
    .then(onSucess)
    .then(r =>{
        console.log(r);
        setComing(true)
    })
