import { Button, FormControl, InputGroup } from 'react-bootstrap';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';
import Coming from './Coming';
import backend from '../../../backend';
import ErrorAlert from '../../utils/components/ErrorAlert';
import { FormattedMessage } from 'react-intl';
import Alert from '../../utils/components/Alert';


const Password = () => {
    const {id, floor} = useParams();
    const [floors, setFloors] = useState(undefined);
    const [token, setToken] = useState();
    const [pass, setPass] = useState();
    const [coming, setComing] = useState(false);
    const [invalidPass, setInvalidPass] = useState(false);
    
    useEffect(() => {
        backend.getFloors(id,setFloors);
    }, [id]);

    return floors ? (coming ? <Coming token={token} floors={floors}/> : <form>
        <FormattedMessage id="project.text.password">{msg => <Alert heading={msg} />}</FormattedMessage>

        <InputGroup>
            <FormControl
                placeholder="password"
                pattern="\d{4}"
                onChange={e => setPass(e.target.value)}
            />
            <InputGroup.Append>
                <Button variant="outline-primary" onClick={_e => {
                    backend.getToken(id, pass, (token) => {
                        setToken(token);
                        backend.move(token, floor, r => {
                            console.log(r);
                            setComing(true);
                        }, e => setComing(true)); // TEA-POT
                    }, e => {setInvalidPass(true)})
                }}>submit</Button>
            </InputGroup.Append>
        </InputGroup>

        {invalidPass && <FormattedMessage id="" >{heading => <FormattedMessage id="">{text => <ErrorAlert onClose={() => setInvalidPass(false)} heading={heading} text={text} />}</FormattedMessage>}</FormattedMessage>
        
        }

    </form>) :  <FormattedMessage id="project.alerts.elevator.notExists" values={{elevator:id}}>{msg => <ErrorAlert text={msg}/>}</FormattedMessage>
};

export default Password;
